        public static void DrawMap(Graphics g)
        {
            g.DrawImage(backgroung, new Rectangle(new Point(0 + ControllerCamera.cameraOffset.X, 0 + ControllerCamera.cameraOffset.Y),
                new Size(3200, 1600)), 0, 0, 3200, 1600, GraphicsUnit.Pixel);

            for (var i = 0; i < mapWidth; i++)
            {
                for (var j = 0; j < mapHeight; j++)
                {
                    // Деревья
                    if (map[i, j] == 1)
                    {
                        g.DrawImage(trees, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 40, 0, 140, 168, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }
                    if (map[i, j] == 2)
                    {
                        g.DrawImage(trees, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 142, 145, 178, 158, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }
                    if (map[i, j] == 3)
                    {
                        g.DrawImage(yellowTrees, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 185, 0, 80, 100, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }
                    if (map[i, j] == 4)
                    {
                        g.DrawImage(yellowTrees, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 45, 180, 95, 125, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }

                    //  Булыжники
                    if (map[i, j] == 5)
                    {
                        g.DrawImage(stones, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 0, 50, 30, 30, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }
                    if (map[i, j] == 6)
                    {
                        g.DrawImage(stones, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 32, 48, 33, 31, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }

                    // Трава
                    if (map[i, j] == 7)
                    {
                        g.DrawImage(grass, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 0, 0, 140, 138, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }
                    if (map[i, j] == 8)
                    {
                        g.DrawImage(grass, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 160, 0, 170, 138, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }
                    if (map[i, j] == 9)
                    {
                        g.DrawImage(grass, new Rectangle(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y),
                            new Size(sizeMap, sizeMap)), 326, 0, 169, 138, GraphicsUnit.Pixel); // 0, 0 - координаты выризаемой области; 20, 20 размеры выризаемой области
                        var objectMap = new ObjectsMap(new Point(j * sizeMap + ControllerCamera.cameraOffset.X, i * sizeMap + ControllerCamera.cameraOffset.Y), new Size(sizeMap, sizeMap));
                        objectsMap.Add(objectMap);
                    }
                }
            }
        }

        public static int GetWidth()
        {
            return sizeMap * mapWidth;
        }
        public static int GetHight()
        {
            return sizeMap * mapHeight;
        }
    }
